mapper.insert:

    SELECT IFNULL(MAX(seq), 0) + 1 AS seq
    FROM ecomms.tsy_partner_conn


INSERT INTO ecomms.tsy_partner_conn (
    partner_id
    ,site_cd
    ,device_cd
    ,client_ip
    ,user_agent
    ,referrer_url
    ,pcid
    ,uid
    ,url_parameter
) VALUES (
    #{partnerId}
    ,#{siteCd}
    ,#{deviceCd}
    ,#{clientIp}
    ,#{userAgent}
    ,#{refererUrl}
    ,#{pcid}
    ,#{uid}
    ,#{urlParameter}
)

mapper.select:
SELECT *
FROM ecomms.tmk_gate_mapping mapping
WHERE 1=1

    AND mapping.site_cd = #{siteCd}


    AND mapping.device_cd = #{deviceCd}


    AND (LCASE(mapping.type) = LCASE(#{type}) OR LCASE(mapping.type) = 'home')

SELECT *
FROM ecomms.t_partnercommi partner
WHERE partner.partnerid = #{partnerId}
